void init_filter()
{
    R(0, 0) = 62.5;
    ekf.C[0] = 1;
    ekf.C[1] = 10000;
    ekf.C[2] = 100;
    ekf.C[3] = 20;
    ekf.X[0] = 0.0;
    ekf.P(0, 0) = 40000.0;
    ekf.Cov_U(0, 0) = 250000.0;
    ekf.setMeasurementFunction(h,1);
    ekf.initted = true;
}
